import React,{ useState } from "react"; 
import './App.css';
import NameForm from './compoent/shoplist';
import Todo from './compoent/Todo';

function App() {
  const [inputText, setInputText ] = useState("");
  const [todos, setTodos ] = useState([]);
  return (
    <div className="App">

      <header>
      <h1>Todo List</h1>
    </header>


      < NameForm todos={todos} setTodos ={setTodos} setInputText={setInputText}></ NameForm>
     <Todo></Todo>
    </div>
  );
}

export default App;
